def send():

	for bot in bots:

		message = client.messages.create(
		                              body=msg,
		                              from_=bot,
		                              to=target
		                          )
		
		print("║   \033[38;23;255;255;255mSending attack command to bot ---> " + bot + ' :: ' + message.status + '  ║')
		

def attack():
	print('╔══════════════════════════════════════════════════════════════╗')
	for x in range(count):
		send()
